percentageCalculation=((e,n)=>parseFloat(Math.round(100*n)/e).toFixed(0)),sortParticipants=((e,n)=>{let t=parseInt(e.positive)+parseInt(e.negative),a=parseInt(n.positive)+parseInt(n.negative);return percentageCalculation(t,e.negative)-percentageCalculation(a,n.negative)});const requestAjax=(e,n,t="get",a=null)=>{"get"==t&&fetch(e,{method:"get"}).then(e=>e.json()).then(e=>{document.getElementById("ranking-wrapper").innerHTML=n(e.data)}).catch(function(e){console.error(e)})},makeHtml=e=>{let n="";return e.sort((e,n)=>sortParticipants(e,n)).forEach((e,t)=>{let a=parseInt(e.positive)+parseInt(e.negative);n+=`\n            <div class="rank-item tooltip">\n                <div class="row">\n                    <div class="image">\n                        <img src="${e.picture}"\n                            class="img-fluid" alt="">\n                        <span>${t+1}</span>\n                    </div>\n                    <div class="content-box">\n                        <h2>${e.name}</h2>\n                        <p>${e.description}</p>\n                    </div>\n                </div>\n\n                <div class="tooltiptext">\n                    <div class="positive">\n                        <h3>Gostam</h3>\n                        <p>${a&&e.positive&&percentageCalculation(a,e.positive)||0}% </p>\n                    </div>\n                    <div class="negative">\n                        <h3>NÃ£o gostam</h3>\n                        <p>${a&&e.negative&&percentageCalculation(a,e.negative)||0}% </p>\n                    </div>\n                </div>\n            </div>\n        `}),[n]};requestAjax("./fazenda.json",makeHtml);